const modelData = JSON.parse(objects[0].getBehavior("ObjectAnimator")._getModelJSON());

for (const box in modelData.box) {
    const objectData = modelData.box[box].anm;
    const object = runtimeScene.createObject(objectData.object.objName);

    object.getVariables().get("__GDanitorAnimator").getChild("UUID").setString(box);
    gdjs.evtTools.linkedObjects.linkObjects(runtimeScene, object, objects[0]);


    object.setAngle(objectData.angle + objects[0].getAngle());
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Angle").setNumber(objectData.angle);

    object.setPosition(gdjs.evtTools.common.getXFromAngleAndDistance(gdjs.evtTools.common.angleBetweenPositions(0, 0, objectData.position.X * objects[0].getScaleX(), objectData.position.Y * objects[0].getScaleY()) + objects[0].getAngle(), gdjs.evtTools.common.distanceBetweenPositions(0, 0, objectData.position.X * objects[0].getScaleX(), objectData.position.Y * objects[0].getScaleY())) + objects[0].getX(), gdjs.evtTools.common.getYFromAngleAndDistance(gdjs.evtTools.common.angleBetweenPositions(0, 0, objectData.position.X * objects[0].getScaleX(), objectData.position.Y * objects[0].getScaleY()) + objects[0].getAngle(), gdjs.evtTools.common.distanceBetweenPositions(0, 0, objectData.position.X * objects[0].getScaleX(), objectData.position.Y * objects[0].getScaleY())) + objects[0].getY());
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("X").setNumber(objectData.position.X);
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Y").setNumber(objectData.position.Y);

    object.setHeight(objectData.height * objects[0].getScaleY());
    object.setWidth(objectData.width * objects[0].getScaleX());
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Height").setNumber(objectData.height);
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Width").setNumber(objectData.width);

    object.setOpacity(objectData.opacity * gdjs.evtTools.common.normalize(objects[0].getOpacity(), 0, 255));
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Opacity").setNumber(objectData.opacity);

    object.flipX(!!objectData.flippedHor);
    object.flipY(!!objectData.flippedVer);
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("FlippedHor").setNumber(objectData.flippedHor);
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("FlippedVer").setNumber(objectData.flippedVer);
}

for (const objectName in objects){
    const object = objects[objectName];
    if (!object.getVariables().get("__GDanitorAnimator").getChild("Started").getAsBoolean()){
        ;
        let animationData = JSON.parse(object.getBehavior("ObjectAnimator")._getAnimationsJSON());
        object.getVariables().get("__GDanitorAnimator").getChild("AnimationData").fromJSObject(animationData);
        object.getVariables().get("__GDanitorAnimator").getChild("Playing").setString(Object.keys(animationData)[0]);
        object.getVariables().get("__GDanitorAnimator").getChild("PlayingData").fromJSObject(animationData[Object.keys(animationData)[0]]);
        object.resetTimer("__GDanitorAnimator_Time");
    }
}

const objectData = objects[0].getVariables().get("__GDanitorAnimator").getChild("Temp").toJSObject();
const partData = objects[0].getVariables().get("__GDanitorAnimator").getChild("IdleData").toJSObject();

let resultAngle = partData.Angle;
if (objectData.FlippedHor){
    resultAngle = 360-partData.Angle
}
objects[0].setAngle((objectData.FlippedVer ? -1 : 1)*resultAngle);

objects[0].setPosition(gdjs.evtTools.common.getXFromAngleAndDistance(gdjs.evtTools.common.angleBetweenPositions(0, 0, partData.X * objectData.ScaleX * (objectData.FlippedHor ? -1 : 1), partData.Y * objectData.ScaleY * (objectData.FlippedVer ? -1 : 1)) + objectData.Angle, gdjs.evtTools.common.distanceBetweenPositions(0, 0, partData.X * objectData.ScaleX * (objectData.FlippedHor ? -1 : 1), partData.Y * objectData.ScaleY * (objectData.FlippedVer ? -1 : 1))) + objectData.X, gdjs.evtTools.common.getYFromAngleAndDistance(gdjs.evtTools.common.angleBetweenPositions(0, 0, partData.X * objectData.ScaleX * (objectData.FlippedHor ? -1 : 1), partData.Y * objectData.ScaleY * (objectData.FlippedVer ? -1 : 1)) + objectData.Angle, gdjs.evtTools.common.distanceBetweenPositions(0, 0, partData.X * objectData.ScaleX * (objectData.FlippedHor ? -1 : 1), partData.Y * objectData.ScaleY * (objectData.FlippedVer ? -1 : 1))) + objectData.Y);

for (const objectName in objects) {
    const object = objects[objectName];
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Angle").setNumber(object.getAngle());

    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("X").setNumber(object.getX());
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Y").setNumber(object.getY());

    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Height").setNumber(object.getHeight());
    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Width").setNumber(object.getWidth());

    object.getVariables().get("__GDanitorAnimator").getChild("IdleData").getChild("Opacity").setNumber(object.getOpacity());
}
